    useEffect(() => {
        if (array===[]) {
            createArray(gridSide)
        }
        createArray(gridSide)
        ref.current=array
        console.log(ref.current)
    });

    return (
        <>
            {array ? array.map((block)=>(<SingleBlock color={block}/>)) : null}
        </>
    );
};

export default Grid;
