import React from 'react';
import {connect} from 'react-redux'

import GameSettings from "./GameSettings";
import {setSettings, setPlayerName, runGame, setMode, createArray} from "../../../redux/actions";

const mapStateToProps = state => {
    return {
        mode: state.settings.mode,
        name: state.settings.name,
        gridSide: state.settings.gridSide,
    };
};

const mapDispatchToProps = dispatch => {
    return {
        setSettings: (mode, gridSide) => dispatch([setMode(mode), createArray(gridSide)]),
        setPlayerName: (name) => dispatch(setPlayerName(name)),
        runGame: () => dispatch(runGame()),
    };
};

const GameSettingsContainer = connect(mapStateToProps, mapDispatchToProps)(GameSettings)

export default GameSettingsContainer;
