import {
    CHOOSE_RANDOM_ELEMENT,
    COMPUTER_SCORE,
    COMPUTER_WINS,
    CREATE_GRID,
    PLAYER_SCORE,
    PLAYER_WINS,
    RUN_GAME
} from "../actions";

const initialSettingsState = {
    runGame: false,
    chosenElements: [],
    array: ['w'],
    playerScore: 0,
    computerScore: 0,
    winner: null
};

const game = (state = initialSettingsState, action) => {
    switch (action.type) {
        case RUN_GAME:
            console.log(action.payload);
            return {
                ...state,
                runGame: true
            };
        case CREATE_GRID:
            return {
                ...state,
                array: action.payload
            }
        case CHOOSE_RANDOM_ELEMENT:
            return {
                ...state,
                chosenElements: state.chosenElements.push(action.payload),
                array: state.array.map(element => element.id === action.payload ?
                    "blue" :
                    element
                )
            }
        case PLAYER_SCORE:
            return {
                ...state,
                playerScore: state.playerScore+1
            }
        case COMPUTER_SCORE:
            return {
                ...state,
                computerScore: state.computerScore+1
            }
        case PLAYER_WINS:
            return {
                ...state,
                winner: "player",
                runGame: false
            }
        case COMPUTER_WINS:
            return {
                ...state,
                winner: "computer",
                runGame: false
            }
        default:
            return state
    }
};



export default game;
