import React, {useEffect} from 'react';

import SingleBlock from "../SingleBlock"

const Grid = ({array, gridSide, createArray}) => {

    useEffect(() => {
        if (array===[]) {
            createArray(gridSide)
        }
        createArray(gridSide)
    });

    return (
        <div>
            {array ? array.map((block)=>(
                    <SingleBlock color={block}/>
                )) : createArray(gridSide)}
        </div>
    );
};

export default Grid;
