import React, {useState} from 'react';
import { connect } from 'react-redux';

import {setSettings} from "../../../redux/actions"
import {ModeInput, NameInput, Option, PlayButton} from "./GameSettings.styled";

const GameSettings = ({mode, name, setPlayerName, setSettings, runGame}) => {

    return (
        <>
            <ModeInput value={mode} onChange={(e)=>setSettings(e.target.value)} >
                <Option name='easyMode'>easy Mode</Option>
                <Option name='normalMode' selected>normalMode</Option>
                <Option name='hardMode'>hardMode</Option>
            </ModeInput>

            <NameInput value={name} onChange={(e)=>setPlayerName(e.target.value)} />
            <PlayButton disablled={name} onClick={()=>runGame()}/>
        </>
    );
};

export default GameSettings;
