import {setTimer} from "../../services/services";
import {array} from "prop-types";

export const SET_EASY_MODE = 'SET_EASY_MODE';
export const SET_NORMAL_MODE = 'SET_NORMAL_MODE';
export const SET_HARD_MODE = 'SET_HARD_MODE';
export const RUN_GAME = 'RUN_GAME';
export const CREATE_GRID = 'CREATE_GRID';
// export const CHANGE_GRID = 'CHANGE_GRID';
export const CHOOSE_RANDOM_ELEMENT = 'CHOOSE_RANDOM_ELEMENT';
export const CHANGE_COLOR_TO_GREEN = 'CHANGE_COLOR_TO_GREEN';
export const CHANGE_COLOR_TO_RED = 'CHANGE_COLOR_TO_RED';
export const PLAYER_SCORE = 'PLAYER_SCORE';
export const COMPUTER_SCORE = 'COMPUTER_SCORE';
export const PLAYER_WINS = 'PLAYER_WINS';
export const COMPUTER_WINS = 'COMPUTER_WINS';

export const setSettings = (mode) => {
    return (dispatch, getState) => {
        const {gridSide} = getState().settings;
        dispatch(setMode(mode))
        dispatch(createArray(gridSide))
    }
};

export const setMode = (mode) => {
    if (mode==='easyMode') {
        return {
            type: SET_EASY_MODE,
            payload: {
                sideSize: 5,
                delay: 2000,
            }
        }
    } else if (mode==='normalMode') {
        return {
            type: SET_NORMAL_MODE,
            payload: {
                sideSize: 10,
                delay: 1000,
            }
        }
    } else return {
        type: SET_HARD_MODE,
        payload: {
            sideSize: 15,
            delay: 900,
        }
    }
};

export const runGame = () => {
    return {
        type: RUN_GAME,
    }
};

// export const changeGrid = () => {
//     return {
//         type: CHANGE_GRID,
//     }
// };

export const createArray = (gridSide) => {
    const newArray = []
    while (newArray.length<gridSide*gridSide) {
        newArray.push("white")
    }
    return {
        type: CREATE_GRID,
        array: newArray
    }
}

export const chooseRandom = (array, chosen) => {
    let elementId = Math.floor(Math.random() * array.length);
    if (chosen.includes(elementId)){
        chooseRandom()
    } else {
        setTimer()
        return {
            type: CHOOSE_RANDOM_ELEMENT,
            elementId: elementId
        }
    }
}

export const submitColorChange = (color) => {
    return (dispatch, getState) => {
        const {playerScore, computerScore, array} = getState().game;
        dispatch(changeBlockColor(color))
        dispatch(calculateScore(color))
        dispatch(checkWinner(playerScore, computerScore,array))
    }
}

export const changeBlockColor = (color, elementId) => {
    if (color==="green"){
        return {
            type: CHANGE_COLOR_TO_GREEN,
            color: color
        }
    } else if (color==="red"){
        return {
            type: CHANGE_COLOR_TO_RED,
            color: color
        }
    }
};

export const calculateScore = (color) => {
    if (color==="green"){
        return {
            type: PLAYER_SCORE
        }
    } else if (color==="red"){
        return {
            type: COMPUTER_SCORE
        }
    }
}

export const checkWinner = (playerScore, computerScore, array) => {
    if (playerScore>=(Math.ceil(array.length/2))){
        return {
            type: PLAYER_WINS
        }
    } else if (computerScore>=(Math.ceil(array.length/2))){
        return {
            type: COMPUTER_WINS
        }
    }
}
