import styled from 'styled-components';

const ScalableGrid = styled.div `
    width: ${props=>props.gridSide*10};
    display: grid;
    grid-column-gap: 0;
    grid-template-columns: repeat(10, 1fr);
`;

export {ScalableGrid}
