import SingleBlock from "../SingleBlock"

const Grid = ({array, gridSide, createArray}) => {

    const ref = useRef();

    useEffect(() => {
        // if (array===[]) {
        //     createArray(gridSide)
        // }

        ref.current = array

        console.log(createArray(gridSide))
    },[array]);
    const prevCount = ref.current;


    return (
        <>
            {console.log(array)}
            {console.log(prevCount)}
            {array ? array.map((block)=>(<SingleBlock color={block}/>)) : null}
        </>
    );
};

export default Grid;
