import React, {useState} from 'react';

import {ModeInput, NameInput, Option, Container, WinnerText} from "./GameSettings.styled";

import PlayButton from "../../common/PlayButton";

const GameSettings = ({winner, mode, name, gameActive, setPlayerName, setSettings, runGame}) => {

    const [playerName, setName] = useState(0);

    const onClick = () => {
        runGame()
        setName()
    }

    return (
        <>
            <Container>
                <ModeInput disabled={gameActive} defaultValue={mode} onChange={(e)=>setSettings(e.target.value)} >
                    <Option name='easy Mode'>easy Mode</Option>
                    <Option name='normal Mode'>normal Mode</Option>
                    <Option name='hard Mode'>hard Mode</Option>
                </ModeInput>

                <NameInput value={playerName} onChange={(e)=>setPlayerName(e.target.value)} />
                <PlayButton value={"Play"} disabled={name===''} onClick={runGame}/>
            </Container>
            <WinnerText>{winner ? `${name} wins!` : null}</WinnerText>
        </>
    );
};

export default GameSettings;
